import { useMemo, useState } from "react";

const N = 8;
const idx = (r, c) => r * N + c;
const rc = (i) => [Math.floor(i / N), i % N];
const isDarkCellRC = (r, c) => (r + c) % 2 === 1;

function initBoard() {
  const b = Array(N * N).fill(null);
  // тёмные сверху, светлые снизу — только на тёмных клетках
  for (let r = 0; r < 3; r++)
    for (let c = 0; c < N; c++) if (isDarkCellRC(r, c)) b[idx(r, c)] = "dark";
  for (let r = N - 3; r < N; r++)
    for (let c = 0; c < N; c++) if (isDarkCellRC(r, c)) b[idx(r, c)] = "light";
  return b;
}

function belongs(piece, player) {
  return piece && piece === player;
}

// Валидные ходы: одна диагональ вперёд, без взятий (MVP)
function deriveValidMoves(board, selected, player) {
  if (selected == null) return new Set();
  const me = board[selected];
  if (!belongs(me, player)) return new Set();

  const [r, c] = rc(selected);
  const dir = player === "dark" ? +1 : -1; // "вперёд" для каждой стороны
  const moves = [];
  for (const dc of [-1, +1]) {
    const nr = r + dir,
      nc = c + dc;
    if (nr >= 0 && nr < N && nc >= 0 && nc < N) {
      const to = idx(nr, nc);
      if (board[to] == null) moves.push(to);
    }
  }
  return new Set(moves);
}

function Square({ children, onSquareClick, dark, selected, hint }) {
  return (
    <button
      className={
        "square" +
        (dark ? " square--dark" : " square--light") +
        (selected ? " square--selected" : "") +
        (hint ? " square--hint" : "")
      }
      onClick={onSquareClick}
    >
      {children}
    </button>
  );
}

function Piece({ color }) {
  return (
    <span
      className={"piece " + (color === "dark" ? "piece--dark" : "piece--light")}
    />
  );
}

function Board({ player, squares, selected, onSelect, onPlay }) {
  const validMoves = useMemo(
    () => deriveValidMoves(squares, selected, player),
    [squares, selected, player]
  );

  function handleClick(i) {
    const piece = squares[i];

    // клик по своей фишке — просто выделяем
    if (piece) {
      if (belongs(piece, player)) onSelect(i);
      else onSelect(null);
      return;
    }

    // клик по пустой клетке — ход, если разрешено
    if (selected != null && validMoves.has(i)) {
      const next = squares.slice();
      next[i] = squares[selected];
      next[selected] = null;
      onPlay(next); // Родитель обновит историю и "очередь" хода
      onSelect(null); // снимаем выделение
    } else {
      onSelect(null);
    }
  }

  return (
    <>
      <div className="status">Turn: {player === "dark" ? "dark" : "light"}</div>
      {[...Array(N)].map((_, r) => (
        <div className="board-row" key={r}>
          {[...Array(N)].map((_, c) => {
            const i = idx(r, c);
            const dark = isDarkCellRC(r, c);
            const piece = squares[i];
            return (
              <Square
                key={i}
                dark={dark}
                selected={i === selected}
                hint={!piece && deriveCellHint(validMoves, i)}
                onSquareClick={() => handleClick(i)}
              >
                {piece && <Piece color={piece} />}
              </Square>
            );
          })}
        </div>
      ))}
    </>
  );
}

function deriveCellHint(validMoves, i) {
  return validMoves.has(i);
}

export default function Game() {
  const [history, setHistory] = useState([initBoard()]);
  const [currentMove, setCurrentMove] = useState(0);
  const [selected, setSelected] = useState(null);

  const player = currentMove % 2 === 0 ? "dark" : "light";
  const currentSquares = history[currentMove];

  function handlePlay(nextSquares) {
    const nextHistory = [...history.slice(0, currentMove + 1), nextSquares];
    setHistory(nextHistory);
    setCurrentMove(nextHistory.length - 1);
  }

  function jumpTo(move) {
    setCurrentMove(move);
    setSelected(null);
  }

  const moves = history.map((_, move) => (
    <li key={move}>
      <button onClick={() => jumpTo(move)} disabled={move === currentMove}>
        {move ? `Go to move #${move}` : "Go to start"}
      </button>
    </li>
  ));

  return (
    <div className="game">
      <div className="game-board">
        <Board
          player={player}
          squares={currentSquares}
          selected={selected}
          onSelect={setSelected}
          onPlay={handlePlay}
        />
      </div>
      <div className="game-info">
        <ol>{moves}</ol>
      </div>
    </div>
  );
}
